ruleToc.chapterList:
+@js:doc = org.jsoup.Jsoup.parse(result);
html = "";
if (result.match(/data-id="(\d+)"/)) {
	num = result.match(/查看隐藏章节\((\d+)\)/)[1];
	p = parseInt(num/900);
	for (var j = 1; j <= p + 1; j++) {
		bid = result.match(/data-bid="(\d+)"/)[1];
		page = j;
		url = "https://www.zhaishuyuan.com/api/";
		body = "action=list&bid=" + bid + "&page=" + page;
		option = {
			"method": "POST",
			"body": String(body)
		}
		json = JSON.parse(java.ajax(url + "," + JSON.stringify(option))).data;
		for (var i = 0; i < json.length; i++) {
			html += '<li><a href="/chapter/' + bid + '/' + (json[i].id - bid) + '" target="_blank">' + json[i].cN + '</a> ' + json[i].uT + '</li>'
		}
	}
	doc.select("#more-chapter").before(html).remove()
}
doc.select("#readerlist li")

ruleSearch.bookList:
.grid tr@tag.a.0<js>
result
  .toArray()
  .slice(0, 3)
  .map(function (a) {
    var url = String(a.attr('href')).replace(
      /^\/([^\/])/,
      baseUrl.match(/(.+\/\/.+?\/)/)[1] + '$1'
    );
    var J = org.jsoup.Jsoup.parse(String(java.ajax(url)));
    var og = (selector) =>
      J.select('[property="og:' + selector + '"]').attr('content');

    return {
      name: og('novel:book_name'),
      author: og('novel:author'),
      kind: og('novel:category') + ',' + og('novel:status'),
      latest: og('novel:latest_chapter_name') + ' ' + String(J.select('#info p').last().text()).replace(/.*：/, '').replace(/(\d+-\d+-\d+).*/, '$1').replace(new RegExp('(·s*)?([^\\d-])?(\\d{2})?'+String(new Date().getFullYear()).slice(-2)+'-'),'$1$2'),
      cover: og('image'),
      intro: J.select('#intro p').eq(0).text(),
      url: url,
    };
  });
</js>

