ruleToc.chapterList:
+@js:
doc=org.jsoup.Jsoup.parse(result);
html="";
if(result.match(/data-id="(\d+)"/)){
num=result.match(/查看隐藏章节\((\d+)\)/)[1];
p=parseInt(num/900);
for(var j=1;j<=p+1;j++){
bid=result.match(/data-bid="(\d+)"/)[1];
page=j;
url="https://www.zhaishuyuan.com/api/";
body="action=list&bid="+bid+"&page="+page;
option={
"method":"POST",
"body":String(body)
}
json=JSON.parse(java.ajax(url+","+JSON.stringify(option))).data;
for(var i=0; i<json.length; i++){ 				
html += '<li><a href="/chapter/'+bid+'/'+(json[i].id - bid)+'" target="_blank">'+json[i].cN+'</a> '+json[i].uT+'</li>';
}
}
doc.select("#more-chapter").before(html).remove();}
doc.select("#readerlist li")

ruleSearch.bookList:
tbody tr!0
<js>
result.toArray().slice(0, 3).map(el => {
	var url = String(el.select('a:eq(0)').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
	var J = org.jsoup.Jsoup.parse(java.ajax(url));
	var o = selector => String(J.select(selector).text());
	var og = selector => String(J.select('[property="og:' + selector + '"]').attr('content'));
	return {
		name: og('novel:book_name').replace(/(全文|免费阅读|最新章节).*|[(（].*[）)]/, ''),
		author: og('novel:author'),
		kind: og('novel:category') + ',' + og('novel:status').replace(/中|已/, ''),
    wordCount: o('.booktag').match(/.+字数：(.+)阅读.+/)[1],
		latest: og('novel:latest_chapter_name').replace(/[\(（【].*[求更谢乐发推].*/, '') + ' • ' + og('novel:update_time').replace(/(T|\s).*/, ' ').replace(/\//g, '-'),
		intro: o('#bookIntro').replace(/.*(观看小说:|简介：)|各位书友.*/, '').replace(/\s+/g, ''),
		cover: og('image'),
		url: url,
	};
}); 
</js>

