        64 != C && (d += String.fromCharCode(e));
    return (function(r) {
      for (var o, t = '', e = 0, a = 0, n = 0; e < r.length; )
        (a = r.charCodeAt(e)) < 128
          ? ((t += String.fromCharCode(a)), e++)
