if(baseUrl.indexOf('page')>-1){
page=baseUrl.match(/page=(\d+)/)[1];

cid=baseUrl.match(/(\d+)\.html/)[1];

bid=baseUrl.match(/http:\/\/www.xs63.com\/.*?\/(.*?)\/\d+\.html/)[1];

id=result.match(/var keys1 = '(\d+)'\+'(\d+)';/);

key=result.match(/var keyss = keys1 \+ '(\d+)'/)[1];

keys=id[1]+id[2]+key;

var time = parseInt(Date.now()/1000);

url='http://www.xs63.com/index/bookinfo/?cid='+cid+'&bid='+bid+'&page='+page+'&keys='+keys+'&keyss='+keys+'&time='+time;

header={"Referer":baseUrl}

post={
"method":"POST",
"headers":JSON.stringify(header)
}

java.getString('@@id.htmlContent@tag.p!-2@html',false)+JSON.parse(java.ajax(url+','+JSON.stringify(post))).data;
}
else{result=java.getString('@@id.htmlContent@tag.p!-2@html',false)}

