function chunkArr (arr, size) {
    if (!arr.length || !size || size < 1) return [];
    let [start, end, result] = [null, null, []];
    for (let i = 0; i < Math.ceil(arr.length / size); i++) {
        start = i * size;
        end = start + size;
        result.push(arr.slice(start, end));
    }
    return result;
}
